INSERT INTO `posts` (`id`, `user_id`, `category_id`, `title`, `alias`, `is_active`, `ordering`, `thumbnail`, `sumary`, `content`, `created`, `modified`, `view_count`) VALUES
(1, 0, 2, 'Test 1', 'test-1', 1, 0, '/img/images/Desert.jpg', 'Tóm tắt bài viết', '<p>\r\n	Nội dung b&agrave;i viết ở đ&acirc;y</p>\r\n', '2013-11-05', '2013-11-05', 0),
(2, 0, 12, 'Chung kết giải thưởng Holcim Prize năm 2013', 'chung-ket-giai-thuong-holcim-prize-nam-2013', 1, 0, '/img/images/holcim2013_1.jpg', 'Trong 2 ngày 25 và 26 tháng 10 năm 2013, tại Trường Đại học Khoa học Xã hội và nhân văn Tp.HCM đã diễn ra Vòng chung kết Giải thưởng Holcim Prize năm 2013. Vòng Chung kết gồm có 07 đề tài xuất sắc nhất đại diện cho 7 trường Đại học của cả nước: Trường ĐH KHXH và NV Tp.HCM, Trường ĐH Kiến trúc Tp.HCM, trường ĐH SPKT Tp.HCM, trường ĐHBK Tp.HCM, Trường ĐH Cần Thơ, Trường ĐHBK - ĐHĐN và Trường ĐH Kiến trúc Hà Nội', '<p>\r\n	<span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; line-height: 19px; text-align: justify;">Trong 2 ng&agrave;y 25 v&agrave; 26 th&aacute;ng 10 năm 2013, tại Trường Đại học Khoa học X&atilde; hội v&agrave; nh&acirc;n văn Tp.HCM đ&atilde; diễn ra V&ograve;ng chung kết Giải thưởng Holcim Prize năm 2013. V&ograve;ng Chung kết gồm c&oacute; 07 đề t&agrave;i xuất sắc nhất đại diện cho 7 trường Đại học của cả nước: Trường ĐH KHXH v&agrave; NV Tp.HCM, Trường ĐH Kiến tr&uacute;c Tp.HCM, trường ĐH SPKT Tp.HCM, trường ĐHBK Tp.HCM, Trường ĐH Cần Thơ, Trường ĐHBK - ĐHĐN v&agrave; Trường ĐH Kiến tr&uacute;c H&agrave; Nội. Đ&acirc;y l&agrave; những đề t&agrave;i xuất sắc nhất được lựa chọn từ hằng trăm đề t&agrave;i v&agrave; trải qua 3 v&ograve;ng lựa chọn.</span></p>\r\n<p style="text-align: center;">\r\n	<span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; line-height: 19px; text-align: justify;"><img alt="" src="/app/webroot/img/images/holcim2013_1.jpg" style="width: 600px; height: 400px;" /></span></p>\r\n<p>\r\n	<span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; line-height: 19px; text-align: justify;">Năm nay, đại diện cho trường Đại học B&aacute;ch khoa, Đại học Đ&agrave; Nẵng tham dự V&ograve;ng chung kết l&agrave; nh&oacute;m sinh vi&ecirc;n 09NL gồm c&aacute;c bạn: Đo&agrave;n Nguyễn V&acirc;n Hiếu, Trương Thế Minh v&agrave; Tạ Bảo Long với đề t&agrave;i&nbsp;</span><strong style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; line-height: 19px; text-align: justify;">&ldquo;Sản xuất Panel l&agrave;m trần c&aacute;ch nhiệt từ c&aacute;c phế phẩm n&ocirc;ng nghiệp&rdquo;</strong><span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; line-height: 19px; text-align: justify;">&nbsp;v&agrave; đ&atilde; xuất sắc nhận được Giải thưởng&nbsp;</span><strong style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; line-height: 19px; text-align: justify;">&ldquo;Bảo vệ m&ocirc;i trường&rdquo;</strong><span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; line-height: 19px; text-align: justify;">.</span></p>\r\n<p style="text-align: center;">\r\n	<span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; line-height: 19px; text-align: justify;"><img alt="" src="/app/webroot/img/images/holcim2013_2.jpg" style="width: 600px; height: 400px;" /></span></p>\r\n<p>\r\n	<span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; line-height: 19px; text-align: justify;">Đ&acirc;y l&agrave; lần thứ 2 trường ĐHBK - ĐHĐN tham dự Giải thưởng n&agrave;y, năm 2012 trường ta đoạt Giải khuyến kh&iacute;ch. C&oacute; thể n&oacute;i Giải thưởng Holcim l&agrave; một Giải thưởng NCKH d&agrave;nh cho sinh vi&ecirc;n rất hấp dẫn, c&oacute; gi&aacute; trị lớn v&agrave; đ&atilde; c&oacute; t&aacute;c động t&iacute;ch cực đến phong tr&agrave;o NCKH của sinh vi&ecirc;n. Giải thưởng năm 2014 sẽ được ph&aacute;t động v&agrave;o cuối th&aacute;ng 10 năm 2013 v&agrave; kết th&uacute;c v&agrave;o dịp n&agrave;y năm sau. Nh&agrave; trường mong muốn sẽ c&oacute; nhiều bạn sinh vi&ecirc;n đăng k&yacute; tham gia giải thưởng n&agrave;y, mang đến cuộc thi nhiều đề t&agrave;i hấp dẫn v&agrave; đoạt Giải thưởng lớn.</span></p>\r\n<p style="text-align: center;">\r\n	<span style="color: rgb(51, 51, 51); font-family: Arial, sans-serif; font-size: 13px; line-height: 19px; text-align: justify;"><img alt="" src="/app/webroot/img/images/holcim2013_3.jpg" style="width: 600px; height: 400px;" /></span></p>\r\n', '2013-11-07', '2013-11-07', 0),
(3, 0, 8, 'VÀI NÉT VỀ TIỂU SỬ CỤ TIỂU LA – NGUYỄN THÀNH                 (1863 -1911)', 'vai-net-ve-tieu-su-cu-tieu-la-nguyen-thanh-1863-1911', 1, 0, '/img/images/Tieu%20La.JPG', 'Chí sĩ Nguyễn Thành hay còn gọi là Nguyễn Hàm, dân quanh vùng quen gọi Ấn Hàm, tự là Triết Phu, hiệu là Tiểu La. Quê ở làng Thạnh Mỹ, huyện Lễ Dương, phủ Thăng Bình, tỉnh Quảng Nam. (Nay là thôn Quý Thạnh, xã Bình Quý, huyện Thăng Bình, tỉnh Quảng Nam)\r\n            Ông xuất thân trong một gia đình quan lại Triều Nguyễn, con trai Bố chánh Kinh lược sứ Nguyễn Trường, dưới triều vua Tự Đức, sau được bổ chức Kinh lược nha sơn phòng sứ tỉnh Bình Định.', '<p align="center" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt; text-indent: 0.5in;">\r\n	<strong><span style="font-size: 11pt;"><font size="2">V&Agrave;I N&Eacute;T VỀ TIỂU SỬ&nbsp;</font></span></strong><strong><span style="font-size: 11pt;"><font size="2">CỤ TIỂU LA &ndash; NGUYỄN TH&Agrave;NH</font></span></strong></p>\r\n<div align="center" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<strong><span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(1863 -1911)</span></strong></div>\r\n<div style="font-family: Tahoma, Arial, Helvetica; font-size: 11px;">\r\n	<table align="left" cellpadding="0" cellspacing="0" hspace="0" vspace="0">\r\n		<tbody>\r\n			<tr>\r\n				<td align="left" style="border: rgb(236, 233, 216); padding: 0in; background-color: transparent;" valign="top">\r\n					<div style="margin: 0in 0in 0pt; vertical-align: baseline; line-height: 37.9pt;">\r\n						<span style="font-size: 49pt;">C</span></div>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<div style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	&nbsp;</div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">h&iacute; sĩ Nguyễn Th&agrave;nh hay c&ograve;n gọi l&agrave; Nguyễn H&agrave;m, d&acirc;n quanh v&ugrave;ng quen gọi Ấn H&agrave;m, tự l&agrave; Triết Phu, hiệu l&agrave; Tiểu La. Qu&ecirc; ở l&agrave;ng Thạnh Mỹ, huyện Lễ Dương, phủ Thăng B&igrave;nh, tỉnh Quảng Nam. (Nay l&agrave; th&ocirc;n Qu&yacute; Thạnh, x&atilde; B&igrave;nh Qu&yacute;, huyện Thăng B&igrave;nh, tỉnh Quảng Nam)</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &Ocirc;ng xuất th&acirc;n trong một gia đ&igrave;nh quan lại Triều Nguyễn, con trai Bố ch&aacute;nh Kinh lược sứ Nguyễn Trường, dưới triều vua Tự Đức, sau được bổ chức Kinh lược nha sơn ph&ograve;ng sứ tỉnh B&igrave;nh Định.</span></div>\r\n<div style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt; text-align: center;">\r\n	<span style="font-size: 11pt;"><img alt="" src="/app/webroot/img/images/Tieu%20La.JPG" style="width: 500px; height: 667px;" /></span></div>\r\n<p align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Thuở nhỏ, Nguyễn Th&agrave;nh nổi tiếng th&ocirc;ng minh, hiếu học. Trong t&aacute;c phẩm &ldquo;Người chủ mưu phong tr&agrave;o du học Nhật Bản&rdquo; Phan Bội Ch&acirc;u viết: &ldquo;Ti&ecirc;n sinh Nguyễn Th&agrave;nh l&uacute;c nhỏ rất th&ocirc;ng minh, đỉnh ngộ, lớn l&ecirc;n c&oacute; t&iacute;nh khẳng kh&aacute;i kh&aacute;c thường, đầu ti&ecirc;n học chữ H&aacute;n nghe hiểu nhớ nhiều, hơn cả bạn b&egrave;&rdquo;. &Ocirc;ng theo cha học văn lẫn v&otilde; ở B&igrave;nh Định. Đến năm 1885 về kinh dự thi Hương. Năm ấy, vụ biến kinh th&agrave;nh Huế nổ ra, kinh th&agrave;nh thất thủ. Từ đ&oacute; &Ocirc;ng g&aacute;c b&uacute;t nghi&ecirc;n, cống hiến cả đời m&igrave;nh cho sự nghiệp cứu nước, giải ph&oacute;ng d&acirc;n tộc.</span></p>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Hưởng ứng hịch Cần Vương (9/1885) do Phụ ch&iacute;nh đại thần T&ocirc;n Thất Thuyết ph&ograve; gi&aacute; vua H&agrave;m Nghi chạy ra T&acirc;n Sở - Quảng Trị, xuống chiếu k&ecirc;u gọi nh&acirc;n d&acirc;n &ldquo;Cần Vương kh&aacute;ng Ph&aacute;p&rdquo;. Tại qu&ecirc; nh&agrave; Quảng Nam, Nghĩa hội Quảng Nam ra đời. Cuộc họp đầu ti&ecirc;n được tổ chức tại th&ocirc;n Ph&ograve; Nam, x&atilde; Ho&agrave; Li&ecirc;n, huyện Ho&agrave; Vang, tiến sĩ Trần Văn Dư được đại hội bầu l&agrave;m thủ lĩnh Nghĩa Hội, ph&oacute; bảng Nguyễn Duy Hiệu l&agrave;m ph&oacute; su&yacute; (so&aacute;i), cử nh&acirc;n Phan B&aacute; Phiến &ndash; nguy&ecirc;n Tri huyện Ph&ugrave; C&aacute;t l&agrave;m tham mưu Nghĩa Hội. Với l&ograve;ng dạt d&agrave;o t&igrave;nh y&ecirc;u qu&ecirc; hương, y&ecirc;u đất nước, Nguyễn Th&agrave;nh liền dấn th&acirc;n v&agrave;o con đường cứu nước v&agrave; tham gia dưới ngọn cờ Nghĩa Hội.</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Do uy t&iacute;n v&agrave; t&agrave;i năng, cụ Nguyễn Th&agrave;nh được Nghĩa Hội uỷ th&aacute;c trao chức T&aacute;n tướng qu&acirc;n vụ ki&ecirc;m Tham biện tỉnh vụ Quảng Nam, địa b&agrave;n hoạt động từ Thăng B&igrave;nh đến Quảng Ng&atilde;i. Chỉ trong mấy năm, Nghĩa qu&acirc;n dưới quyền chỉ huy của cụ đ&atilde; lập được nhiều căn cứ với nhiều chiến c&ocirc;ng ở s&ocirc;ng Vu Gia, Thu Bồn, Đ&agrave; Nẵng&hellip;g&acirc;y nhiều tiếng vang, khiến cho thực d&acirc;n ph&aacute;p phải k&iacute;nh nể.</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Năm 1887, Nghĩa qu&acirc;n của Mai Xu&acirc;n Thưởng bị bao v&acirc;y r&aacute;o riết ở chiến trường Ng&atilde;i - Định. Nguyễn Th&agrave;nh đ&atilde; đưa qu&acirc;n v&agrave;o tiếp ứng, qu&acirc;n của &ocirc;ng đ&atilde; gi&uacute;p Nghĩa qu&acirc;n của Mai Xu&acirc;n Thưởng tho&aacute;t v&ograve;ng v&acirc;y của địch v&agrave; củng cố Nghĩa Hội ở hai tỉnh Quảng Ng&atilde;i, B&igrave;nh Định.</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Thế nhưng, v&igrave; qu&acirc;n &iacute;t thế c&ocirc;, vũ kh&iacute; th&ocirc; sơ lại kh&ocirc;ng qua trường lớp huấn luyện cơ bản, phải đối ph&oacute; trước qu&acirc;n đội tinh nhuệ h&ugrave;ng mạnh với vũ kh&iacute; hiện đại n&ecirc;n Phong tr&agrave;o Cần Vương ở Nam - Ng&atilde;i - Định bước v&agrave;o thời kỳ tan r&atilde;. Nguyễn Th&agrave;nh đưa qu&acirc;n về ứng cứu cho Nghĩa Hội Quảng Nam. Tr&ecirc;n đường r&uacute;t qu&acirc;n, &ocirc;ng bị giặc chặn đ&aacute;nh v&agrave; bắt tại Cầu Ch&aacute;y, B&igrave;nh Sơn - Quảng Ng&atilde;i.</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tại nh&agrave; giam Quảng Ng&atilde;i, Nguyễn Th&agrave;nh khước từ mọi &acirc;m mưu mua chuộc, h&ograve;ng l&ocirc;i k&eacute;o &Ocirc;ng cộng t&aacute;c với Ph&aacute;p do Nguyễn Th&acirc;n thực hiện. Cuối c&ugrave;ng, nhằm l&agrave;m xoa dịu v&agrave; để thuyết phục con em giới sĩ phu lớp trước, kẻ th&ugrave; buộc phải đưa &ocirc;ng về quản th&uacute;c tại qu&ecirc; nh&agrave;.</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Từ đ&acirc;y Nguyễn Th&agrave;nh bắt đầu thời kỳ 15 năm&nbsp;<strong><em>mai danh ẩn t&iacute;ch</em></strong>, nhưng sục s&ocirc;i l&ograve;ng y&ecirc;u nước. Tại qu&ecirc; nh&agrave; &Ocirc;ng lập &ldquo;Sơn trang trại Nam Thịnh&rdquo;, bề ngo&agrave;i l&agrave; một tổ chức sản xuất với th&uacute; điền vi&ecirc;n, b&ecirc;n trong &Ocirc;ng b&iacute; mật li&ecirc;n kết với sĩ phu văn th&acirc;n y&ecirc;u nước như: T&aacute;n Hai L&ecirc; Vĩnh Huy, T&aacute;n Tương Đỗ Đăng Tuyển, T&aacute;n Nh&igrave; (&Ocirc;ng &Iacute;ch Nh&igrave;), T&aacute;n Thiện (&Ocirc;ng &Iacute;ch Thiện), Ch&acirc;u Thượng Văn&hellip;nhất l&agrave; kết t&igrave;nh giao du với c&aacute;c nh&agrave; khoa bảng y&ecirc;u nước như: Ch&iacute; sĩ Phan Chu Trinh, Huỳnh Th&uacute;c Kh&aacute;ng, L&ecirc; B&aacute; Trinh, Phan Th&uacute;c Duyện&hellip; mượn cớ l&agrave; đến ch&uacute;c mừng c&aacute;c vị ấy đỗ vị thứ cao trong khoa thi Hương năm Canh T&yacute; (1900).</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tiếng l&agrave;nh đồn xa, từ năm 1903 ch&iacute; sĩ Phan Bội Ch&acirc;u - Người anh t&agrave;i của Xứ Nghệ nghe tiếng b&egrave;n nhiều lần t&igrave;m đến thăm hỏi c&ugrave;ng với cụ&nbsp;<strong><em>&ldquo;mật nghị quốc sự&rdquo;</em></strong>. Với cuộc gặp lịch sử n&agrave;y, phong tr&agrave;o giải ph&oacute;ng d&acirc;n tộc ở nước ta bước sang một trang sử mới.</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Th&aacute;ng 5/1904, tại Sơn trang trại Nam Thịnh, Nguyễn Th&agrave;nh c&ugrave;ng với Phan Bội Ch&acirc;u v&agrave; nhiều sĩ phu y&ecirc;u nước kh&aacute;c đ&atilde; khai sinh ra Duy T&acirc;n hội, tiền th&acirc;n của Phong tr&agrave;o Đ&ocirc;ng Du, tiền th&acirc;n của Việt Nam Quang phục hội Xứ Nghệ v&agrave; Xứ Quảng. Nguyễn Th&agrave;nh lấy hiệu Tiểu La, b&iacute; mật vận động kinh t&agrave;i, tổ chức thương hội v&agrave; đưa thanh ni&ecirc;n sang Nhật du học với mưu cầu cứu nước, giải ph&oacute;ng d&acirc;n tộc sau n&agrave;y.</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Năm 1908, Phong tr&agrave;o Duy T&acirc;n l&ecirc;n cao như s&oacute;ng nước triều d&acirc;ng, cuộc biểu t&igrave;nh kh&aacute;ng sưu đầu ti&ecirc;n nổ ra ở Đại Lộc nhanh ch&oacute;ng lan rộng ra khắp tỉnh Quảng Nam v&agrave; c&aacute;c tỉnh Trung Kỳ m&agrave; sử quen gọi l&agrave; &ldquo;Trung Kỳ d&acirc;n biến&rdquo;. Thực d&acirc;n Ph&aacute;p thẳng tay đ&agrave;n &aacute;p, hầu hết c&aacute;c nh&agrave; y&ecirc;u nước đều bị bắt giết hoặc bị t&ugrave; đ&agrave;y khổ sai. Cụ Tiểu La cũng kh&ocirc;ng tr&aacute;nh khỏi, cụ bị bắt v&agrave; kết tội &ldquo;theo đảng bội quốc, ngầm th&ocirc;ng với nước ngo&agrave;i, đề xướng d&acirc;n quyền&rdquo; kết &aacute;n 9 năm t&ugrave; biệt xứ đi C&ocirc;n Đảo.</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ra đảo được mấy th&aacute;ng, được tin tại qu&ecirc; nh&agrave; vợ mất, rồi sau đ&oacute; người con g&aacute;i qua đời. Kế tiếp l&agrave; hung tin, Nhật cấu kết với Ph&aacute;p trục xuất du học sinh Việt Nam v&agrave; Phan Bội Ch&acirc;u ra khỏi Nhật Bản. Đau x&oacute;t trước viễn cảnh<strong><em>&ldquo;Nước mất nh&agrave; tan&rdquo;</em></strong>, Cụ L&acirc;m bệnh thổ huyết v&agrave; mất tại C&ocirc;n Đảo v&agrave;o ng&agrave;y 11/11/1911 khi tuổi đời chỉ mới 48 v&agrave; m&atilde;i hơn 50 năm sau (1957), con ch&aacute;u cụ Tiểu La mới đưa h&agrave;i cốt của cụ về an t&aacute;ng tại qu&ecirc; nh&agrave;.</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Khi ở trong ngục, d&ugrave; bị h&agrave;nh hạ cụ vẫn c&oacute; những c&acirc;u văn c&ugrave;ng với những b&agrave;i thơ chữ H&aacute;n, vĩnh quyết đồng bạn thật đậm đ&agrave; chua x&oacute;t: &ldquo;Thời cuộc xoay vần, cơ hội tốt sẽ đến, gi&ocirc;ng m&acirc;y Đ&ocirc;ng &Aacute; sau n&agrave;y c&ograve;n nhiều cuộc biến đổi, anh em h&atilde;y gắng l&ecirc;n&rdquo;. B&agrave;i thơ chữ H&aacute;n đ&atilde; được ch&iacute; sĩ Huỳnh Th&uacute;c Kh&aacute;ng dịch, trong đ&oacute; c&oacute; đoạn:</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ldquo; Một việc chưa th&agrave;nh t&oacute;c nhuộm m&agrave;u</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Non s&ocirc;ng ngoảnh lại thẹn m&agrave;y r&acirc;u.</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &hellip;</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Mở toang hai mắt tr&ocirc;ng trời đất</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ngắm thử mười năm vẫn thế ru&rdquo;</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; V&ocirc; c&ugrave;ng thương tiếc trước c&aacute;i chết của người Ch&iacute; sĩ y&ecirc;u nước t&agrave;i hoa, ch&iacute; sĩ L&ecirc; B&aacute; Trinh kh&oacute;c cụ Tiểu La qua mấy vần thơ:</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bạn b&egrave; chuyện tr&ograve; sầu qu&ecirc;n l&atilde;ng</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size: 11pt;">Y&ecirc;u ma gặp gỡ n&oacute;i &ocirc;n tồn</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Thương người cỡi gi&oacute; miền C&ocirc;n Đảo</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size: 11pt;">Tiếc bạn v&ugrave;i th&acirc;n chốn suối v&agrave;ng.</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Đ&aacute;nh gi&aacute; về th&acirc;n thế sự nghiệp c&ugrave;ng ch&acirc;n t&agrave;i của Tiểu La được thể hiện qua b&agrave;i Văn tế kh&oacute;c Tiểu La của ch&iacute; sĩ S&agrave;o Nam &ndash; Phan Bội Ch&acirc;u, c&oacute; đoạn:</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size: 11pt;">&ldquo;Hỡi &ocirc;i, Tiểu La anh ơi!...</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt; text-indent: 0.5in;">\r\n	<span style="font-size: 11pt;">Nghĩ một người đ&agrave;o tạo ra em, th&agrave;nh tựu cho em, m&agrave; b&acirc;y giờ kẻ mất người c&ograve;n hồn trời ph&aacute;ch đất!</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt; text-indent: 0.5in;">\r\n	<span style="font-size: 11pt;">M&acirc;y Hải V&acirc;n mờ mịt b&oacute;ng gương v&agrave;ng</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt; text-indent: 0.5in;">\r\n	<span style="font-size: 11pt;">Bể Đ&agrave; Nẵng chập chờn cơn s&oacute;ng bạc!...</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt; text-indent: 0.5in;">\r\n	<span style="font-size: 11pt;">Than &ocirc;i!</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt; text-indent: 0.5in;">\r\n	<span style="font-size: 11pt;">Lịch sử anh thế n&agrave;o! Nh&acirc;n c&aacute;ch anh thế n&agrave;o! những ai l&agrave; người, c&oacute; mắt, c&oacute; tai, chắc đều biết thảy.</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt; text-indent: 0.5in;">\r\n	<span style="font-size: 11pt;">Em xin kể lại mấy c&acirc;u, c&ograve;n tường thuật thuỷ chung em đ&atilde; viết v&agrave;o bản &ldquo;Việt Nam nghĩa sĩ liệt&rdquo; bằng H&aacute;n văn rồi vậy&hellip; &rdquo;</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Một bậc ch&acirc;n t&agrave;i như ch&iacute; sĩ S&agrave;o Nam đ&atilde; kh&ocirc;ng tiếc lời ca ngợi về Tiểu La như thế, ch&uacute;ng ta đủ biết vai tr&ograve; v&agrave; vị tr&iacute; của Tiểu La - Nguyễn Th&agrave;nh trong phong tr&agrave;o y&ecirc;u nước cuối thế kỷ XIX đầu thế kỷ XX.</span></div>\r\n<div align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;</span><span style="font-size: 11pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<font size="1"><strong>Tổng lược v&agrave; bi&ecirc;n soạn:</strong></font></span></div>\r\n<p align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;"><font size="1">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Huỳnh Văn Chung</font></span></p>\r\n<p align="justify" style="font-family: Tahoma, Arial, Helvetica; font-size: 11px; margin: 0in 0in 0pt;">\r\n	<span style="font-size: 11pt;"><font size="1">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tổ: Sử - Địa - GDCD - Trường THPT Tiểu La</font></span></p>\r\n', '2013-11-13', '2013-11-13', 0),
(4, 0, 12, 'Thông báo Khẩn V/v phòng chống siêu bão Haiyan', 'thong-bao-khan-vv-phong-chong-sieu-bao-haiyan', 1, 0, '', '', '<div class="detail_title" style="margin: 0px; padding: 0px 10px; font-family: Arial; font-weight: bold; font-size: 18px; color: rgb(0, 51, 153); width: 670px;">\r\n	<span style="font-size:12px;"><a href="http://www.udn.vn/posts/view/1045/59#" name="title_post" style="margin: 0px; padding: 0px; text-decoration: none; color: blue; outline: 0px;">Th&ocirc;ng b&aacute;o Khẩn V/v ph&ograve;ng chống si&ecirc;u b&atilde;o Haiyan (Hải Yến)</a>&nbsp;<span style="margin: 0px; padding: 0px; color: rgb(0, 0, 0); font-style: italic;">(08-11-2013)</span></span></div>\r\n<div class="detail_conts" style="margin: 8px 0px 0px; padding: 10px; width: 670px; font-family: Arial; font-size: 0.8em; color: rgb(51, 51, 51); text-align: justify; line-height: 25px;">\r\n	<p style="margin: 0px; padding: 0px;">\r\n		&nbsp;</p>\r\n	<table border="0" cellpadding="0" cellspacing="0" style="margin: 0px; padding: 0px;">\r\n		<tbody style="margin: 0px; padding: 0px;">\r\n			<tr style="margin: 0px; padding: 0px;">\r\n				<td style="margin: 0px; padding: 0px; width: 266px;">\r\n					<p align="center" style="margin: 0px; padding: 0px;">\r\n						<span style="font-size:12px;">BỘ GI&Aacute;O DỤC V&Agrave; Đ&Agrave;O TẠO</span></p>\r\n					<p align="center" style="margin: 0px; padding: 0px;">\r\n						<span style="font-size:12px;"><strong style="margin: 0px; padding: 0px;">ĐẠI HỌC Đ&Agrave; NẴNG</strong></span></p>\r\n					<p align="center" style="margin: 0px; padding: 0px;">\r\n						<span style="font-size:12px;"><strong style="margin: 0px; padding: 0px;">_________________</strong></span></p>\r\n				</td>\r\n				<td style="margin: 0px; padding: 0px; width: 398px;">\r\n					<p align="center" style="margin: 0px; padding: 0px;">\r\n						<span style="font-size:12px;"><strong style="margin: 0px; padding: 0px;">CỘNG H&Ograve;A X&Atilde; HỘI CHỦ NGHĨA VIỆT NAM</strong></span></p>\r\n					<p align="center" style="margin: 0px; padding: 0px;">\r\n						<span style="font-size:12px;"><strong style="margin: 0px; padding: 0px;">Độc lập &ndash; Tự do &ndash; Hạnh ph&uacute;c</strong></span></p>\r\n					<p align="center" style="margin: 0px; padding: 0px;">\r\n						<span style="font-size:12px;"><strong style="margin: 0px; padding: 0px;">_______________________________________________________________</strong></span></p>\r\n				</td>\r\n			</tr>\r\n			<tr style="margin: 0px; padding: 0px;">\r\n				<td style="margin: 0px; padding: 0px; width: 266px;">\r\n					<p align="center" style="margin: 0px; padding: 0px;">\r\n						<span style="font-size:12px;">Số: 7012 /ĐHĐN-VP</span></p>\r\n				</td>\r\n				<td style="margin: 0px; padding: 0px; width: 398px;">\r\n					<p align="center" style="margin: 0px; padding: 0px;">\r\n						<span style="font-size:12px;"><em style="margin: 0px; padding: 0px;">Đ&agrave; Nẵng, ng&agrave;y 08 th&aacute;ng 11 năm 2013</em></span></p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n	<p style="margin: 0px; padding: 0px;">\r\n		&nbsp;</p>\r\n	<p style="margin: 0px; padding: 0px;">\r\n		<span style="font-size:12px;"><strong style="margin: 0px; padding: 0px;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Khẩn</strong></span></p>\r\n	<p align="center" style="margin: 0px; padding: 0px;">\r\n		<span style="font-size:12px;"><strong style="margin: 0px; padding: 0px;">TH&Ocirc;NG B&Aacute;O SỐ 1</strong></span></p>\r\n	<p align="center" style="margin: 0px; padding: 0px;">\r\n		<span style="font-size:12px;"><strong style="margin: 0px; padding: 0px;">V/v ph&ograve;ng chống si&ecirc;u b&atilde;o Haiyan (Hải Yến)</strong></span></p>\r\n	<p align="center" style="margin: 0px; padding: 0px;">\r\n		<span style="font-size:12px;">_________________________</span></p>\r\n	<p align="center" style="margin: 0px; padding: 0px;">\r\n		&nbsp;</p>\r\n	<p align="center" style="margin: 0px; padding: 0px;">\r\n		&nbsp;</p>\r\n	<p align="center" style="margin: 0px; padding: 0px;">\r\n		&nbsp;</p>\r\n	<p align="center" style="margin: 0px; padding: 0px;">\r\n		<span style="font-size:12px;">K&iacute;nh gửi: C&aacute;c trường th&agrave;nh vi&ecirc;n, c&aacute;c đơn vị trực thuộc</span></p>\r\n	<p style="margin: 0px; padding: 0px;">\r\n		&nbsp;</p>\r\n	<p style="margin: 0px; padding: 0px;">\r\n		<span style="font-size:12px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Theo Trung t&acirc;m Dự b&aacute;o kh&iacute; tượng Thủy văn Trung ương, l&uacute;c 13 giờ chiều nay 7-11, vị tr&iacute; t&acirc;m b&atilde;o ở v&agrave;o khoảng 9,3 độ Vĩ Bắc; 131,1 độ Kinh Đ&ocirc;ng, c&aacute;ch đảo Mindanao (Philippines) khoảng 520 km về ph&iacute;a Đ&ocirc;ng. Sức gi&oacute; mạnh nhất ở v&ugrave;ng gần t&acirc;m b&atilde;o mạnh cấp 17 (tức l&agrave; từ 202 đến 221 km một giờ), giật tr&ecirc;n cấp 17. Dự b&aacute;o trong 24 giờ tới, b&atilde;o di chuyển nhanh theo hướng giữa T&acirc;y v&agrave; T&acirc;y T&acirc;y Bắc, mỗi giờ đi được khoảng 30 - 35 km. Đến 13 giờ ng&agrave;y 8-11, vị tr&iacute; t&acirc;m b&atilde;o ở v&agrave;o khoảng 11,5 độ Vĩ Bắc; 123,4 độ Kinh Đ&ocirc;ng, tr&ecirc;n khu vực miền Trung Philippines. Sức gi&oacute; mạnh nhất v&ugrave;ng gần t&acirc;m b&atilde;o mạnh cấp 16, cấp 17 (tức l&agrave; từ 184 đến 221 km một giờ), giật tr&ecirc;n cấp 17.</span><br style="margin: 0px; padding: 0px;" />\r\n		&nbsp;</p>\r\n	<p style="margin: 0px; padding: 0px;">\r\n		<span style="font-size:12px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trưởng Ban chỉ đạo ph&ograve;ng chống lụt b&atilde;o Trung ương, Bộ trưởng Bộ N&ocirc;ng nghiệp v&agrave; Ph&aacute;t triển n&ocirc;ng th&ocirc;n Cao Đức Ph&aacute;t đặc biệt lưu &yacute;, si&ecirc;u b&atilde;o Haiyan l&agrave; cơn b&atilde;o mạnh nhất trong v&ograve;ng 10 năm gần đ&acirc;y, b&atilde;o hiện đang ở cấp 17 v&agrave; di chuyển rất nhanh. Dự b&aacute;o sẽ đổ bộ v&agrave;o đất liền c&aacute;c tỉnh miền Trung trong khoảng thời gian từ tối ng&agrave;y 10 đến rạng s&aacute;ng ng&agrave;y 11-11. Khi v&agrave;o bờ, b&atilde;o ở cấp 12 đến 13, giật cấp 14 đến 15.</span><br style="margin: 0px; padding: 0px;" />\r\n		&nbsp;</p>\r\n	<p style="margin: 0px; padding: 0px;">\r\n		<span style="font-size:12px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Si&ecirc;u b&atilde;o Haiyan, diễn biến phức tạp, khả năng ảnh hưởng đến th&agrave;nh phố Đ&agrave; Nẵng l&agrave; rất lớn. C&aacute;c trường th&agrave;nh vi&ecirc;n, đơn vị trực thuộc cần quyết liệt triển khai c&aacute;c biện ph&aacute;p ứng ph&oacute; si&ecirc;u b&atilde;o, đảm bảo an to&agrave;n t&iacute;nh mạng cho CBVC v&agrave; HSSV, giảm thiểu thấp nhất thiệt hại về t&agrave;i sản trước, trong v&agrave; sau b&atilde;o. Trước mắt, Đại học Đ&agrave; Nẵng y&ecirc;u cầu c&aacute;c trường th&agrave;nh vi&ecirc;n, đơn vị trực thuộc nghi&ecirc;m t&uacute;c, khẩn trương thực hiện c&aacute;c c&ocirc;ng việc sau đ&acirc;y:</span><br style="margin: 0px; padding: 0px;" />\r\n		&nbsp;</p>\r\n	<p style="margin: 0px; padding: 0px;">\r\n		<span style="font-size:12px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1. Lập phương &aacute;n sơ t&aacute;n HSSV đang tr&uacute; ở c&aacute;c ph&ograve;ng trọ kh&ocirc;ng ki&ecirc;n cố b&ecirc;n ngo&agrave;i nh&agrave; trường v&agrave;o tr&uacute; ở những nơi ki&ecirc;n cố như k&yacute; t&uacute;c x&aacute;, giảng đường,&hellip; b&ecirc;n trong nh&agrave; trường để tr&aacute;nh tr&uacute; b&atilde;o.</span><br style="margin: 0px; padding: 0px;" />\r\n		&nbsp;</p>\r\n	<p style="margin: 0px; padding: 0px;">\r\n		<span style="font-size:12px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2. Ban ph&ograve;ng chống thi&ecirc;n tai của đơn vị gấp r&uacute;t cử c&aacute;n bộ xuống c&aacute;c địa điểm xung yếu (giảng đường, nh&agrave; xưởng, k&yacute; t&uacute;c x&aacute;, ph&ograve;ng l&agrave;m việc, ph&ograve;ng th&iacute; nghiệm, ph&ograve;ng m&aacute;y t&iacute;nh, thư viện, nh&agrave; tập thể dục thể thao, v.v&hellip;) của đơn vị kiểm tra, đ&ocirc;n đốc triển khai c&aacute;c phương &aacute;n ph&ograve;ng chống b&atilde;o (đặc biệt kiểm tra kỹ c&aacute;c cửa sổ, cửa ra v&agrave;o, c&aacute;c v&aacute;ch nh&ocirc;m k&iacute;nh, m&aacute;i t&ocirc;n, v.v&hellip;); huy động lực lượng, cơ sở vật chất, phương tiện kỹ thuật sẵn s&agrave;ng ứng ph&oacute; với c&aacute;c t&igrave;nh huống phức tạp xảy ra.</span><br style="margin: 0px; padding: 0px;" />\r\n		&nbsp;</p>\r\n	<p style="margin: 0px; padding: 0px;">\r\n		<span style="font-size:12px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3. Theo d&otilde;i s&aacute;t t&igrave;nh h&igrave;nh diễn biến của si&ecirc;u b&atilde;oHaiyan, phối hợp chặt chẽ với lực lượng ph&ograve;ng chống lụt b&atilde;o v&agrave; t&igrave;m kiếm cứu nạn địa phương v&agrave; Đại học Đ&agrave; Nẵng, kịp thời ứng ph&oacute; với c&aacute;c t&igrave;nh huống phức tạp xảy ra.</span><br style="margin: 0px; padding: 0px;" />\r\n		&nbsp;</p>\r\n	<p style="margin: 0px; padding: 0px;">\r\n		<span style="font-size:12px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 4. Thực hiện nghi&ecirc;m t&uacute;c chế độ th&ocirc;ng tin, b&aacute;o c&aacute;o nhanh về Đại học Đ&agrave; Nẵng (qua số điện thoại của &ocirc;ng L&ecirc; Th&agrave;nh Bắc, Ch&aacute;nh Văn ph&ograve;ng: 0913423178); đặc biệt khi c&oacute; c&aacute;c sự cố bất thường.</span><br style="margin: 0px; padding: 0px;" />\r\n		&nbsp;</p>\r\n	<p style="margin: 0px; padding: 0px;">\r\n		<span style="font-size:12px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tr&acirc;n trọng./.</span></p>\r\n	<p style="margin: 0px; padding: 0px;">\r\n		&nbsp;</p>\r\n	<table border="0" cellpadding="0" cellspacing="0" style="margin: 0px; padding: 0px;">\r\n		<tbody style="margin: 0px; padding: 0px;">\r\n			<tr style="margin: 0px; padding: 0px;">\r\n				<td style="margin: 0px; padding: 0px; width: 321px;">\r\n					<p style="margin: 0px; padding: 0px;">\r\n						<span style="font-size:12px;"><strong style="margin: 0px; padding: 0px;"><em style="margin: 0px; padding: 0px;">Nơi nhận:</em></strong></span></p>\r\n					<p style="margin: 0px 0px 0px 36.85pt; padding: 0px;">\r\n						<span style="font-size:12px;">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Như tr&ecirc;n;</span></p>\r\n					<p style="margin: 0px 0px 0px 36.85pt; padding: 0px;">\r\n						<span style="font-size:12px;">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Lưu VT.</span></p>\r\n				</td>\r\n				<td style="margin: 0px; padding: 0px; width: 320px;">\r\n					<p align="center" style="margin: 0px; padding: 0px;">\r\n						<span style="font-size:12px;"><strong style="margin: 0px; padding: 0px;">GI&Aacute;M ĐỐC</strong></span></p>\r\n					<p align="center" style="margin: 0px; padding: 0px;">\r\n						&nbsp;</p>\r\n					<p align="center" style="margin: 0px; padding: 0px;">\r\n						&nbsp;</p>\r\n					<p align="center" style="margin: 0px; padding: 0px;">\r\n						<span style="font-size:12px;">(đ&atilde; k&yacute;)</span></p>\r\n					<p align="center" style="margin: 0px; padding: 0px;">\r\n						&nbsp;</p>\r\n					<p align="center" style="margin: 0px; padding: 0px;">\r\n						&nbsp;</p>\r\n					<p align="center" style="margin: 0px; padding: 0px;">\r\n						<span style="font-size:12px;"><strong style="margin: 0px; padding: 0px;">PGS.TS Trần Văn Nam</strong></span></p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</div>\r\n<p>\r\n	&nbsp;</p>\r\n', '2013-11-13', '2013-11-13', 0);
